import React, {useState} from "react";
import NewsItems from './newsItems';
const NewsList = () => {
    const[articles, setArticles] = useState([ ]);
    const[query, setQuery] = useState("");
    const[message, setMessage] = useState("");

    const apiKey = '10509121f8ba4f998f7d43c436836cf5';

    const getNews = () => {
        fetch(`https://newsapi.org/v2/everything?q=${query}&apiKey=${apiKey}`)
        .then(res => res.json())
        .then(data => {
            setQuery("")
            setArticles(data.articles)
            console.log(data);
        })
        .catch(error => {
            setMessage(error);
        })
    }

    return(
    <div>
        <h1 style={{fontSize:"20px", color:"red"}} className="alert-hd">{message}</h1>
        <div className="news-box">
            <input type="text" className="news-input" placeholder="Search..." 
            value={query} onChange={e => setQuery(e.target.value)}/><br/>
            <br/>
            <input type="button" className="newsBtn" value="Explore" onClick={getNews}/>        
        </div>
        <div className="news-list">
            {articles.map(({title, description, url, urlToImage}) => (
                <NewsItems
                title={title}
                description={description}
                url={url}
                urlToImage={urlToImage}/>
            ))}
        </div>
    </div>
    );
}
export default NewsList;
